<script>
    "use strict";

    $(document).on('click', '#logoutLink', function(e) {
        e.preventDefault();

        Swal.fire({
            title: '{{\App\Utils\translate('Do you want to logout')}}?',
            showDenyButton: true,
            showCancelButton: true,
            confirmButtonColor: '#FC6A57',
            cancelButtonColor: '#363636',
            confirmButtonText: `{{\App\Utils\translate('Yes')}}`,
            denyButtonText: `{{\App\Utils\translate('Don\'t Logout')}}'`,
        }).then((result) => {
            if (result.value) {
                window.location.href = '{{route('admin.auth.logout')}}';
            } else {
                Swal.fire('{{\App\Utils\translate('Canceled')}}', '', '{{\App\Utils\translate('info')}}');
            }
        });
    });
    $(document).on('click', '.menu-mobile', function(e) {
        e.preventDefault();
        // change style propriety hs-unfold-hidden
        $('#accountNavbarDropdown').toggleClass('hs-unfold-hidden');
    });
</script>
@endpush
